package com.wahab.books_junction;

public class Books
{
     String name;
     int img;

    public Books(String name, int img)
    {
        // TODO Auto-generated constructor stub
        this.name = name;
        this.img = img;
    }

    public String getName()
    {
        return this.name;
    }

    public int getImg()
    {
        return this.img;
    }
}